import React, { useRef, useState } from 'react'; // 6.9k (gzipped: 2.7k)

import '../styles/tour-details.css'
import { Container, Row, Col, Form, ListGroup } from 'reactstrap'
import { useParams } from 'react-router-dom'
import tourData from '../assets/data/tours'
import calculateAvgRating from './../utils/avgRating';
import avatar from '../assets/images/avatar.jpg';
import Booking from '../components/Booking/Booking';


const TourDetails = () => {

    const { id } = useParams();
    const reviewMsgRef = useRef('');
    const [tourRating, setTourRating] = useState(null);

    // Tìm tour theo id
    const tour = tourData.find(tour => tour.id === id);

    // ✅ Kiểm tra nếu không có tour thì return giao diện báo lỗi
    if (!tour) {
        return (
            <section className="text-center py-5">
                <Container>
                    <h2>Tour not found</h2>
                    <p>The tour with ID "{id}" does not exist.</p>
                </Container>
            </section>
        );
    }

    // Destructure chỉ khi tour tồn tại
    const { photo, title, desc, price, address, reviews, city, distance, maxGroupSize } = tour;

    const { avgRating, totalRating } = calculateAvgRating(reviews);

    const options = { day: 'numeric', month: 'long', year: 'numeric' };

    const handleSubmit = (e) => {
        e.preventDefault();
        const reviewText = reviewMsgRef.current.value;
        // Later will call API
    };

    return (
        <>
            <section>
                <Container>
                    <Row>
                        <Col lg='8'>
                            <div className="tour__content">
                                <img src={photo} alt="" />

                                <div className="tour__info">
                                    <h2>{title}</h2>
                                    <div className="d-flex align-items-center gap-5">
                                        <span className="tour__rating d-flex align-items-center gap-1">
                                            <i className="ri-star-s-fill" style={{ color: "var(--secondary-color)" }}></i>
                                            {avgRating === 0 ? null : avgRating}
                                            {totalRating === 0 ? 'Not Rated' : <span>({reviews?.length})</span>}
                                        </span>

                                        <span>
                                            <i className="ri-map-pin-user-fill"></i> {address}
                                        </span>
                                    </div>

                                    <div className="tour__extra-details">
                                        <span><i className="ri-map-pin-2-line"></i> {city}</span>
                                        <span><i className="ri-money-dollar-circle-line"></i> ${price}/per person</span>
                                        <span><i className="ri-map-pin-time-line"></i> {distance} k/m</span>
                                        <span><i className="ri-group-line"></i> {maxGroupSize} people</span>
                                    </div>

                                    <h5>Description</h5>
                                    <p>{desc}</p>
                                </div>

                                {/* Review section */}
                                <div className="tour__reviews mt-4">
                                    <h4>Reviews ({reviews?.length} reviews)</h4>
                                    <Form onSubmit={handleSubmit}>
                                        <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                                            {[1, 2, 3, 4, 5].map(num => (
                                                <span key={num} onClick={() => setTourRating(num)}>
                                                    {num}<i className="ri-star-s-line"></i>
                                                </span>
                                            ))}
                                        </div>

                                        <div className="review__input">
                                            <input
                                                type="text"
                                                ref={reviewMsgRef}
                                                placeholder="Share your thoughts"
                                                required
                                            />
                                            <button className="btn primary__btn text-white" type="submit">Submit</button>
                                        </div>
                                    </Form>

                                    <ListGroup className="user__reviews">
                                        {reviews?.map((review, index) => (
                                            <div className="review__item" key={index}>
                                                <img src={avatar} alt="" />
                                                <div className="w-100">
                                                    <div className="d-flex align-items-center justify-content-between">
                                                        <div>
                                                            <h5>Muhib</h5>
                                                            <p>{new Date('06-16-2025').toLocaleDateString("en-US", options)}</p>
                                                        </div>
                                                        <span className="d-flex align-items-center">
                                                            5<i className="ri-star-s-fill" style={{ color: "var(--secondary-color)" }}></i>
                                                        </span>
                                                    </div>
                                                    <h6>Amazing tour</h6>
                                                </div>
                                            </div>
                                        ))}
                                    </ListGroup>
                                </div>
                            </div>
                        </Col>

                        <Col lg='4'>
                            <Booking tour={tour} avgRating={avgRating} />
                        </Col>
                    </Row>
                </Container>
            </section>
            <Newsletter />
        </>
    );
};

export default TourDetails;
